const inputItem = items[0]; // Get the entire input item
const attachment = inputItem.binary.attachment_0;

if (!attachment) {
  console.error("Attachment 'attachment_0' not found in binary data.");
  return []; // Return an empty array if no attachment to process
}

const line= $input.first().json.headers.subject

const match = line.match(/Grade\s(\d+)/i);

let grade = -1
if (match) {
   grade = parseInt(match[1], 10);
}

return [
  {
    json: {
      grade,
      mimeType: attachment.mimeType, // Put mimeType in JSON for easy access downstream
      fileName: attachment.fileName, 
    },

    binary: {
      data: attachment // Pass the entire attachment object here
    }
  }
];

const inputItem = items[0]; // Get the entire input item

const attachments = inputItem.binary;
const targetFileName = 'Grade ' + $('Code1').first().json.grade + '.pdf'

const targetMimeType = "application/pdf"; // Recommended to also check MIME type

let foundPdfAttachment = null;

// Iterate over all properties in the 'attachments' object
// Each property represents a single attachment (e.g., 'attachment_0', 'attachment_1', 'data')
for (const attachmentKey in attachments) {
  const currentAttachment = attachments[attachmentKey];

  // Ensure the currentAttachment object and its properties exist
  if (currentAttachment && currentAttachment.fileName && currentAttachment.mimeType) {
    // Compare the fileName and mimeType
    if (currentAttachment.fileName === targetFileName && currentAttachment.mimeType === targetMimeType) {
      foundPdfAttachment = currentAttachment;
      break; // Found the specific PDF, no need to check further
    }
  }
}

// Prepare the output for the next node
if (foundPdfAttachment) {
  return [
    {
      json: {
        fileName: foundPdfAttachment.fileName,
        mimeType: foundPdfAttachment.mimeType,
      },
      // CRITICAL: Pass the actual binary data of the found PDF
      // We name it 'data' for consistency with n8n's default binary property
      binary: {
        data: foundPdfAttachment 
      }
    }
  ];
} else {
  // If the specific PDF was not found, return an empty array
  // This will stop the workflow branch or allow a subsequent node to handle no input
  console.log(`PDF with filename "${targetFileName}" and mimeType "${targetMimeType}" not found.`);
  return []; 
}
